import { useEffect, useRef, useState } from "react";
import { carteditems, carteditemsclear, updatecartitem, updatecartproductid } from "../API/CartAPI";
import type { Cart, CartApiResponse, itemupdate, itemwithproductid } from "../types/Cart";
import { Navbar } from "../components/Navbar";
import { SupportAndMore } from "../components/supportandmore";
import { ChevronDown, ChevronUp } from "lucide-react";
